import React, { useContext, useEffect } from "react";
import { UserContext } from "./context/UserContext";
import { useHistory } from "react-router-dom";

const Shipments = () => {
  const history = useHistory();

  const {
    state: { data, loading, called },
    actions: { getShipments, setId },
  } = useContext(UserContext);

  useEffect(() => {
    getShipments();
  }, [called]);

  const deleteS = () => {};

  if (loading) {
    return <p>Loading...</p>;
  }
  return (
    <div>
      <div className="shipment shipmentTitle">
        <p className="title">Waybill</p>
      </div>
      {data &&
        data.user.shipments.map((shipment, key) => (
          <div key={key} className="shipment">
            <p>{key + 1}</p>
            <p>{shipment.waybill}</p>
            <div className="btns">
              <button
                onClick={() => history.pushState(`/update/${shipment.id}`)}
              >
                update
              </button>
              <button onClick={deleteS}>delete</button>
            </div>
          </div>
        ))}
    </div>
  );
};

export default Shipments;
