import React from "react";
import { useLazyQuery, gql } from "@apollo/client";

const GET_SHIPMENTS = gql`
  query ($id: ID!) {
    user(id: $id) {
      shipments {
        id
        waybill
      }
    }
  }
`;

const GetShipments = (id) => {
  const [getShipments, { error, data, loading }] = useLazyQuery(GET_SHIPMENTS, {
    variables: {
      id,
    },
  });

  return {
    error,
    data,
    loading,
  };
};

export default GetShipments;
