import React, { useContext } from "react";
import { useQuery, gql } from "@apollo/client";
import { UserContext } from "./context/UserContext";

const GET_SHIPMENTS = gql`
  query ($id: ID!) {
    user(id: $id) {
      shipments {
        id
        waybill
      }
    }
  }
`;

const MyShipments = () => {
  const {
    state: { waybill },
    actions: { createShipment, setWaybill },
  } = useContext(UserContext);

  const { error, data, loading } = useQuery(GET_SHIPMENTS, {
    variables: {
      id: 2,
    },
  });

  if (loading) {
    return <p>Loading...</p>;
  }
  return (
    <div>
      {data.user.shipments &&
        data.user.shipments.map((shipment, key) => (
          <p key={key}>{shipment.id}</p>
        ))}
    </div>
  );
};

export default MyShipments;
